// main.js

import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import { createI18n } from 'vue-i18n';
import { defineRule, configure } from 'vee-validate';
import { required, email } from '@vee-validate/rules';
import { localize } from '@vee-validate/i18n';
import pt_BR from '@vee-validate/i18n/dist/locale/pt_BR.json'; // Importe as mensagens em português

// Adicione as regras de validação necessárias
defineRule('required', required);
defineRule('email', email);

// Configure o vee-validate para utilizar as mensagens em português
configure({
  generateMessage: localize('pt_BR', pt_BR), // Utilize as mensagens em português
});

const i18n = createI18n({
  locale: 'pt_BR', // Defina o idioma como português do Brasil
});

const app = createApp(App);
app.use(router);
app.use(i18n);
app.mount('#app');
