        <div class="form-group">
          <label for="bairro">Bairro:</label>
          <input type="text" id="bairro" v-model="editedContact.bairro" placeholder="Bairro" required>
        </div>

        <div class="form-group">
          <label for="estado">Estado:</label>
          <input type="text" id="estado" v-model="editedContact.estado" placeholder="Estado" required>
        </div>
      </div>

      <!-- Botão de upload da foto -->
      <div class="form-group">
        <label for="photo">Foto (Upload):</label>
        <input type="file" id="photo" ref="photoInput" @change="handleFileUpload">
        <!-- Pré-visualização da foto, se disponível -->
        <div v-if="editedContact.photo" class="photo-preview" :style="{ backgroundImage: 'url(' + getPhotoUrl(editedContact.photo) + ')' }"></div>
      </div>

