import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import { Field, Form, ErrorMessage, defineRule, configure } from 'vee-validate';
import { required, email } from '@vee-validate/rules';
import { localize, setLocale } from '@vee-validate/i18n';
import ptBR from '@vee-validate/i18n/dist/locale/pt_BR.json'; // Importe o arquivo de localização desejado

defineRule('required', required);
defineRule('email', email);

// Configure o VeeValidate
configure({
  generateMessage: localize({ pt_BR: ptBR }), // Defina o idioma padrão
});
setLocale('pt_BR'); // Defina o idioma padrão

const app = createApp(App);
app.use(router);
app.component('Field', Field);
app.component('Form', Form);
app.component('ErrorMessage', ErrorMessage);

app.mount('#app');
